import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/pagination';
import '../css/body/swiper_main.css'
import { Pagination, Autoplay } from 'swiper/modules';
import SwiperCore from 'swiper';
// import SwiperCore, { EffectCoverflow, Pagination, Autoplay } from "swiper";

const SwiperMain = () => {
    SwiperCore.use([Autoplay,Pagination]);

    const styles = {
        swiper_image: {
            width: "100%",
            position: "relative",
            left: "100px",
            top: "-5px",
        },
        // 스타일 오브젝트를 정의하고, 미디어 쿼리를 적용하고자 하는 스타일도 추가합니다.
        // 미디어 쿼리를 사용하려면 이처럼 "@media"를 포함한 스타일 오브젝트를 만들어야 합니다.
        // 이때, 스타일 오브젝트 내부에서 "@media"를 사용할 수 있습니다.
        '@media (max-width: 768px)': {
         swiper_image: {
            left: "0px",
            width: "100%",
          },
        },
      };


    return (
    <>
      <Swiper style={styles.swiper_image}

        pagination={{
          dynamicBullets: true,
          clickable: true,
          el: '.swiper-pagination',
        }}

        className="mySwiper"
        autoplay={{
            delay: 100000, // 3000밀리초(3초)로 딜레이 설정
            disableOnInteraction: false, // 사용자가 Swiper와 상호작용할 때에도 자동 스와이프를 허용
          }}
        loop={true}
      >
        <SwiperSlide>
            <div className='swiper-image'>
                <img src="/swiper_image/ozone.jpeg" alt="ozone" />
            </div>
        </SwiperSlide>
        <SwiperSlide>
            <div className='swiper-image'>
                <img src="/swiper_image/kbs_donghang.jpeg" alt="kbs" />
            </div>
        </SwiperSlide>
        <SwiperSlide>
            <div className='swiper-image'>
                <img src="/swiper_image/careman.jpeg" alt="careman" />
            </div>
        </SwiperSlide>
      </Swiper>
    </>
    );
};

export default SwiperMain;
