import React, { useState } from 'react';
import { BrowserRouter as Router, Link, Route } from 'react-router-dom';
import '../css/header/dropdown.css';

const Dropdown = () => {
  const [showMenu, setShowMenu] = useState(false);

  const handleMenuToggle = () => {
    setShowMenu(!showMenu);
  };

  return (
    <Router>
      <div className="container">
        <div className="menu">
          <button onClick={handleMenuToggle}>급여대여</button>
          {showMenu && (
            <ul className="dropdown">
              <li>
                <Link to="/electric-bed">전동침대</Link>
              </li>
              <li>
                <Link to="/wheelchair">휠체어</Link>
              </li>
              <li>
                <Link to="/mattress">매트리스</Link>
              </li>
              <li>
                <Link to="/portable-bath">이동욕조</Link>
              </li>
            </ul>
          )}
        </div>
      </div>

      <Route path="/electric-bed" component={() => <div>전동침대 페이지</div>} />
      <Route path="/wheelchair" component={() => <div>휠체어 페이지</div>} />
      <Route path="/mattress" component={() => <div>매트리스 페이지</div>} />
      <Route path="/portable-bath" component={() => <div>이동욕조 페이지</div>} />
    </Router>
  );
};

export default Dropdown;
