import React from 'react';
import '../css/header/dropdown.css'

const DropDown = () => {
    const [showMenu, setShowMenu] = useState(); /* 만약 첫파라미터인 showMenu에 초기값 false 할당하고싶으면 useState(false); 하면됨, 두번째 파라미터 setShowMenu 자바의 setter와 같은기능이다 */

    const handleMouseOver = () => {
      setShowMenu(true);
    };

    const handleMouseOut = () => {
      setShowMenu(false);
    };

    return (
        <div className="container">
        <div
          className="menu"
          onMouseOver={handleMouseOver}
          onMouseOut={handleMouseOut}
        >
          <button>급여대여</button>
          {showMenu && (
            <ul className="dropdown">
              <li>
                <Link to="/electric-bed">전동침대</Link>
              </li>
              <li>
                <Link to="/wheelchair">휠체어</Link>
              </li>
              <li>
                <Link to="/mattress">매트리스</Link>
              </li>
              <li>
                <Link to="/portable-bath">이동욕조</Link>
              </li>
            </ul>
          )}
        </div>
        <div
          className="menu"
          onMouseOver={handleMouseOver}
          onMouseOut={handleMouseOut}
        >
          <button>급여대여</button>
          {showMenu && (
            <ul className="dropdown">
              <li>
                <Link to="/electric-bed">전동침대</Link>
              </li>
              <li>
                <Link to="/wheelchair">휠체어</Link>
              </li>
              <li>
                <Link to="/mattress">매트리스</Link>
              </li>
              <li>
                <Link to="/portable-bath">이동욕조</Link>
              </li>
            </ul>
          )}
        </div>
      </div>
    );
};

export default DropDown;
